import { useState, useEffect } from "react";
function App() {
  const [products, setProducts] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  useEffect(() => {
    fetch("https://fakestoreapi.com/products")
      .then((res) => res.json())
      .then((json) => {
        setProducts(json);
        setIsLoading(false);
      });
  }, []);

  if(isLoading){
    return <h1>Loading...</h1>;
  }
  return (
    <div>
      {
        products.map((product) => (
          <div key={product.id}>
            <p>{product.title}</p>
            <p>{product.description}</p>
            <img
              src={product.image}
              alt={product.title}
              height="200px"
              width="200px"
            />
            <hr />
          </div>
        ))
      }
    </div>
  );
}
export default App;
